UPDATE dbc_mailtemplate SET Subject_Lang_koKR='행복한 겨울맞이 축제 되세요!', Body_Lang_koKR='김이 모락모락 나는 따뜻한 사과맛 탄산수를 만들어 친구와 함께 나누며 축제를 즐기세요!' WHERE ID=187;
